document.addEventListener('click',(function(e){e.target.matches('.modal-close-js')&&(document.querySelectorAll('video.video-element').forEach(e=>{e.pause()}),document.querySelectorAll('iframe.video-element[src*="youtube.com/embed"]').forEach(e=>{e.contentWindow.postMessage(JSON.stringify({event:'command',func:'pauseVideo'}),'*')}),document.querySelectorAll('iframe.video-element[src*="vimeo.com"]').forEach(e=>{e.contentWindow.postMessage({method:'pause'},'*')}))}));